        return new Source("{% extends 'base.html.twig' %}

{% block title %}Liste des utilisateur{% endblock %}

{% block body %}
    <h1>Liste des utilisateurs</h1>
     <a href=\"{{ path('user_new') }}\" class=\"btn btn-primary mt-3\"><i class=\"bi bi-plus-circle\"></i> Créer un nouvel utilisateur</a>
    <table class=\"table table-striped table-hover mt-3\">
        <thead>
            <tr>
                <th>Nom</th>
                <th>Prénom</th>
                <th>Email</th>
                <th>Rôles</th>
                <th></th>
            </tr>
        </thead>
        <tbody>
        {% for user in users %}
            <tr>
                <td>{{ user.Name }}</td>
                <td>{{ user.LastName }}</td>
                <td>{{ user.email }}</td>
                <td>{{ user.roles ? user.roles|json_encode : '' }}</td>
                <td class=\"text-end\">
                    <div class=\"btn-group\">
                    <a href=\"{{ path('user_edit', {'id': user.id}) }}\" class=\"btn btn-primary btn-sm\"><i class=\"bi bi-pencil\"></i> Éditer</a>
                    {{ include('user/_delete_form.html.twig') }}
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">Pas d'utilisateurs trouvés</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
{% endblock %}
", "user/index.html.twig", "C:\\Users\\Utilisateur\\Desktop\\projet-maman-getion-eleve\\templates\\user\\index.html.twig");
